import React from 'react';
const baseStyles = {
    width: '100%',
    height: '100%'
};
/*
 * COMPONENTS
 */
const Header = () => React.createElement("div", { style: { width: '6px' } });
const Cell = (instance) => {
    const { isNavigatedCell, webComponentsReactProperties } = instance;
    if (isNavigatedCell) {
        return React.createElement("div", { style: baseStyles, className: webComponentsReactProperties.classes.valueStateInformation });
    }
    return React.createElement("div", { style: baseStyles });
};
/*
 * TABLE HOOKS
 */
const columnsDeps = (deps, { instance: { webComponentsReactProperties } }) => {
    return [...deps, webComponentsReactProperties.withNavigationHighlight];
};
