    }
    return [
        ...currentColumns,
        {
            id: '__ui5wcr__internal_navigation_column',
            disableFilters: true,
            disableSortBy: true,
            disableGroupBy: true,
            disableResizing: true,
            disableDragAndDrop: true,
            width: 6,
            minWidth: 6,
            maxWidth: 6,
            Header,
            Cell
