    ];
};
export const useRowNavigationIndicators = (hooks) => {
    hooks.columns.push(columns);
    hooks.columnsDeps.push(columnsDeps);
    hooks.visibleColumnsDeps.push(visibleColumnsDeps);
    hooks.visibleColumns.push(visibleColumns);
};
