import UI5Element from "@ui5/webcomponents-base/dist/UI5Element.js";
import { isEnter } from "@ui5/webcomponents-base/dist/Keys.js";
import CSSColor from "@ui5/webcomponents-base/dist/types/CSSColor.js";
