			type: Object,
		},

		/**
		 * @private
		 */
		_alpha: {
			type: Float,
			defaultValue: 1,
		},

		/**
		 * @private
		 */
		_hue: {
			type: Integer,
			defaultValue: 0,
		},

		/**
		 * @private
		 */
		_isSelectedColorChanged: {
			type: Boolean,
		},

		/**
		 * @private
		 */
		 _isHueValueChanged: {
			type: Boolean,
		},

		/**
		 * @private
		 */
		_wrongHEX: {
			type: Boolean,
		},
	},
	slots: /** @lends sap.ui.webcomponents.main.ColorPicker.prototype */ {
		//
	},
	events: /** @lends sap.ui.webcomponents.main.ColorPicker.prototype */ {
		/**
		 * Fired when the the selected color is changed
		 *
		 * @event
		 * @public
		 */
		change: {},
	},
