  // EUC_JP.equalsIgnoreCase(PLATFORM_DEFAULT_ENCODING);

  static castAsNonUtf8Char(code: number, encoding: Charset = null) {
    // ISO 8859-1 is the Java default as UTF-8 is JavaScripts
