    // 0 == black, 255 == white (i.e. an 8 bit greyscale bitmap).
    private static renderResult(code: QRCode, width: number /*int*/, height: number /*int*/, quietZone: number /*int*/): BitMatrix {
        const input = code.getMatrix();
        if (input === null) {
