import { Card, Loader, CardHeader, FlexBox, Text, Icon, Table, TableColumn, Label, tabelCe } from '@ui5/webcomponents-react';
import React, { useState, useEffect } from 'react';
import "@ui5/webcomponents-icons/dist/AllIcons.js";

const LoaderComponent = () => {
    const initialText =
        'This is a text that will be updated 5 seconds after the header is clicked. The text will be visible until the new text has been fetched. To let the user know that the text is updating, the Loader will be displayed.';
    const [text, setText] = useState(initialText);
    const [loading, setLoading] = useState(false);
    const onHeaderClick = () => {
        setLoading(true);
    };
    useEffect(() => {
        if (loading) {
            setTimeout(() => {
                setText((prev) => (prev === initialText ? 'Updated (⌐■_■)' : initialText));
                setLoading(false);
            }, 5000);
        }
    }, [loading]);
    return (
        <Card
            header={
                <CardHeader
                    titleText="Click here to update the table below."
                    interactive
                    avatar={<Icon name="activate" />}
                    onClick={onHeaderClick}
                />
            }
            style={{ width: '1200px' }}
        >
            <FlexBox direction="Column">
                {loading && <Loader />}
                {/* <Text style={{}}>{text}</Text> */}
                <Table
                    columns={<><TableColumn style={{ width: '12rem' }}><Label>Product</Label></TableColumn><TableColumn minWidth={800} popinText="Supplier"><Label>Supplier</Label></TableColumn><TableColumn demandPopin minWidth={600} popinText="Dimensions"><Label>Dimensions</Label></TableColumn><TableColumn demandPopin minWidth={600} popinText="Weight"><Label>Weight</Label></TableColumn><TableColumn><Label>Price</Label></TableColumn></>}
                    onLoadMore={function noRefCheck() { }}
                    onPopinChange={function noRefCheck() { }}
                    onRowClick={function noRefCheck() { }}
                    onSelectionChange={function noRefCheck() { }}
                >
                    <TableRow>
                        <TableCell>
                            <Label>
                                Notebook Basic
                            </Label>
                        </TableCell>
                        <TableCell>
                            <Label>
                                Very Best Screens
                            </Label>
                        </TableCell>
                        <TableCell>
                            <Label>
                                30 x 18 x 3cm
                            </Label>
                        </TableCell>
                        <TableCell>
                            <Label>
                                4.2KG
                            </Label>
                        </TableCell>
                        <TableCell>
                            <Label>
                                956EUR
                            </Label>
                        </TableCell>
                    </TableRow>
                    <TableRow>
                        <TableCell>
                            <Label>
                                Notebook Basic 17HT-1001
                            </Label>
                        </TableCell>
                        <TableCell>
                            <Label>
                                Very Best Screens
                            </Label>
                        </TableCell>
                        <TableCell>
                            <Label>
                                29 x 17 x 3.1cm
                            </Label>
                        </TableCell>
                        <TableCell>
                            <Label>
                                4.5KG
                            </Label>
                        </TableCell>
                        <TableCell>
                            <Label>
                                1249EUR
                            </Label>
                        </TableCell>
                    </TableRow>
                </Table>
            </FlexBox>
            {/* <FlexBox>
          {loading && <Loader />}
          <Text>{text}</Text>
        </FlexBox> */}
        </Card>
    );
};
export default LoaderComponent;
