import { boot } from '@ui5/webcomponents-base/dist/Boot.js';
import { getEffectiveScopingSuffixForTag } from '@ui5/webcomponents-base/dist/CustomElementsScope.js';
import { addCustomCSS } from '@ui5/webcomponents-base/dist/Theming.js';
export const addCustomCSSWithScoping = (baseTagName, customCSS) => {
    boot().then(() => {
        const tagNameSuffix = getEffectiveScopingSuffixForTag(baseTagName);
        const finalTag = tagNameSuffix ? `${baseTagName}-${tagNameSuffix}` : baseTagName;
        addCustomCSS(finalTag, customCSS);
    });
};
